    // Setup snap points per mobile
    setupSnapPoints() {
        if (this.isMobile || this.isTablet) {
            // Enable CSS snap scrolling on mobile/tablet
            this.container.style.scrollSnapType = 'x mandatory';
            this.container.style.webkitOverflowScrolling = 'touch'; // Smooth iOS scrolling

            // Add snap points to children
            const children = this.container.children;
            for (let child of children) {
                child.style.scrollSnapAlign = 'start';
                child.style.scrollSnapStop = 'normal';
