            if (!isDragging) return;

            const deltaX = pageX - startX;
            const deltaY = pageY - startY;

            // Determina se è un movimento orizzontale
            if (!isHorizontalDrag && (Math.abs(deltaX) > 10 || Math.abs(deltaY) > 10)) {
