/**
 * Slider Orizzontale Migliorato per Elementor
 * Versione 3.0 - Ottimizzato per Mobile, Desktop e Wide Screen
 */

// Classe principale per gestire lo slider
class ImprovedElementorScroller {
    constructor(container) {
        this.container = container;
        this.wrapper = null;
        this.isScrolling = false;
        this.scrollTimeout = null;
        this.isMobile = window.innerWidth <= 768;
        this.isTablet = window.innerWidth > 768 && window.innerWidth <= 1024;
        this.isWideScreen = window.innerWidth > 1920;

        // Feature detection
        this.supportsPassive = this.checkPassiveSupport();
        this.supportsScrollBehavior = 'scrollBehavior' in document.documentElement.style;
        this.supportsTouch = 'ontouchstart' in window || navigator.maxTouchPoints > 0;

        // Inizializza
        this.init();

        // Setup responsive handler
        this.setupResponsiveHandler();
    }

    // Check supporto passive listeners
    checkPassiveSupport() {
        let supportsPassive = false;
        try {
            const opts = Object.defineProperty({}, 'passive', {
                get: () => { supportsPassive = true; }
            });
            window.addEventListener('testPassive', null, opts);
            window.removeEventListener('testPassive', null, opts);
        } catch (e) {}
        return supportsPassive;
    }

    init() {
        // Crea wrapper per aggiungere controlli senza modificare HTML
        this.wrapContainer();

        // Setup features
        this.setupNavButtons();
        this.setupKeyboard();
        this.setupMouseWheel();
        this.setupTouch();
        this.setupScrollIndicator();
        this.setupAccessibility();
        this.setupSnapPoints();

        // Aggiorna stato iniziale
        this.updateUI();
    }

    // Setup responsive handler
    setupResponsiveHandler() {
        let resizeTimer;
        window.addEventListener('resize', () => {
            clearTimeout(resizeTimer);
            resizeTimer = setTimeout(() => {
                const wasMobile = this.isMobile;
                const wasTablet = this.isTablet;
                const wasWideScreen = this.isWideScreen;

                this.isMobile = window.innerWidth <= 768;
                this.isTablet = window.innerWidth > 768 && window.innerWidth <= 1024;
                this.isWideScreen = window.innerWidth > 1920;

                // Re-init if device type changed
                if (wasMobile !== this.isMobile || wasTablet !== this.isTablet || wasWideScreen !== this.isWideScreen) {
                    this.updateResponsiveFeatures();
                }
            }, 250);
        });
    }

    updateResponsiveFeatures() {
        // Update navigation visibility
        if (this.prevButton && this.nextButton) {
            if (this.isMobile) {
                this.prevButton.style.display = 'none';
                this.nextButton.style.display = 'none';
            } else {
                this.updateUI();
            }
        }

        // Update snap behavior
        this.setupSnapPoints();
    }

    // Wrap container esistente per aggiungere controlli
    wrapContainer() {
        // Se già wrappato, esci
        if (this.container.parentElement.classList.contains('elementor-scroller-wrapper')) {
            this.wrapper = this.container.parentElement;
            return;
        }

        // Crea wrapper
        this.wrapper = document.createElement('div');
        this.wrapper.className = 'elementor-scroller-wrapper';
        this.wrapper.style.position = 'relative';

        // Inserisci wrapper
        this.container.parentNode.insertBefore(this.wrapper, this.container);
        this.wrapper.appendChild(this.container);
    }

    // Setup pulsanti navigazione
    setupNavButtons() {
        // Non mostrare pulsanti su mobile
        if (this.isMobile) return;

        // Crea pulsanti solo se non esistono
        if (!this.wrapper.querySelector('.nav-button')) {
            // Pulsante precedente
            const prevButton = document.createElement('button');
            prevButton.className = 'nav-button nav-prev';
            prevButton.innerHTML = `
                <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2">
                    <path d="M15 18l-6-6 6-6"/>
                </svg>
            `;
            prevButton.setAttribute('aria-label', 'Scorri a sinistra');
            prevButton.addEventListener('click', () => this.scrollPrev());

            // Pulsante successivo
            const nextButton = document.createElement('button');
            nextButton.className = 'nav-button nav-next';
            nextButton.innerHTML = `
                <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2">
                    <path d="M9 18l6-6-6-6"/>
                </svg>
            `;
            nextButton.setAttribute('aria-label', 'Scorri a destra');
            nextButton.addEventListener('click', () => this.scrollNext());

            // Aggiungi al wrapper
            this.wrapper.appendChild(prevButton);
            this.wrapper.appendChild(nextButton);

            // Store references
            this.prevButton = prevButton;
            this.nextButton = nextButton;
        }
    }

    // Setup snap points per mobile
    setupSnapPoints() {
        if (this.isMobile || this.isTablet) {
            // Enable CSS snap scrolling on mobile/tablet
            this.container.style.scrollSnapType = 'x mandatory';
            this.container.style.webkitOverflowScrolling = 'touch'; // Smooth iOS scrolling

            // Add snap points to children
            const children = this.container.children;
            for (let child of children) {
                child.style.scrollSnapAlign = 'start';
                child.style.scrollSnapStop = 'normal';
