    // Setup mouse wheel - MIGLIORATO per wide screen
    setupMouseWheel() {
        if (this.isMobile) return; // Skip on mobile

        let accumulatedDelta = 0;
        let wheelTimeout;
        let lastWheelTime = 0;

        const wheelHandler = (e) => {
            // Solo se container è in hover o focus
            if (!this.container.matches(':hover, :focus-within')) return;

            const now = Date.now();
            const timeDelta = now - lastWheelTime;
            lastWheelTime = now;

            // Prevent default vertical scroll
            e.preventDefault();

            // Calculate dynamic scroll speed based on screen width
            const baseSpeed = this.isWideScreen ? 1.5 : 1;
            const dynamicMultiplier = timeDelta < 100 ? 2 : 1; // Boost for quick scrolls

            // Accumula piccoli delta per scroll più fluido
            accumulatedDelta += e.deltaY * baseSpeed * dynamicMultiplier;

