    }

    // Setup touch con momentum migliorato e supporto mobile
    setupTouch() {
        let startX = 0;
        let startY = 0;
        let scrollLeft = 0;
        let isDragging = false;
        let isHorizontalDrag = false;
        let animationId = null;
        let velocity = 0;
        let lastX = 0;
        let lastTime = 0;
        let touchStartTime = 0;
        let momentumHistory = [];

        const startDragging = (pageX, pageY) => {
            isDragging = true;
            isHorizontalDrag = false;
            startX = pageX;
            startY = pageY;
            lastX = pageX;
            scrollLeft = this.container.scrollLeft;
            lastTime = Date.now();
            touchStartTime = Date.now();
            velocity = 0;
            momentumHistory = [];

            // Cancella animazioni
            if (animationId) {
                cancelAnimationFrame(animationId);
            }

            // Add active class
            this.container.classList.add('active');

            // Disable snap during drag on mobile
            if (this.isMobile || this.isTablet) {
                this.container.style.scrollSnapType = 'none';
            }
        };

        const drag = (pageX, pageY, e) => {
            if (!isDragging) return;

            const deltaX = pageX - startX;
            const deltaY = pageY - startY;

            // Determina se è un movimento orizzontale
            if (!isHorizontalDrag && (Math.abs(deltaX) > 10 || Math.abs(deltaY) > 10)) {
                isHorizontalDrag = Math.abs(deltaX) > Math.abs(deltaY);
            }

            // Se è movimento orizzontale, previeni scroll verticale
            if (isHorizontalDrag && e) {
