        this.container.addEventListener('touchstart', (e) => {
            // Don't interfere with pinch zoom
            if (e.touches.length === 1) {
                startDragging(e.touches[0].pageX, e.touches[0].pageY);
            }
        }, { passive: true });

        this.container.addEventListener('touchmove', (e) => {
            if (e.touches.length === 1) {
                drag(e.touches[0].pageX, e.touches[0].pageY, e);
            }
        }, { passive: false });

        this.container.addEventListener('touchend', stopDragging, { passive: true });
        this.container.addEventListener('touchcancel', stopDragging, { passive: true });
    }

    // Setup indicatore di scroll
    setupScrollIndicator() {
        // Skip progress bar on mobile
        if (this.isMobile) return;

        // Crea progress bar solo se non nascosta da design
        if (!this.wrapper.classList.contains('hide-progress')) {
            const progressBar = document.createElement('div');
            progressBar.className = 'scroll-progress';
            progressBar.setAttribute('role', 'progressbar');
            progressBar.setAttribute('aria-label', 'Progresso scroll');
            progressBar.setAttribute('aria-valuemin', '0');
            progressBar.setAttribute('aria-valuemax', '100');

            this.wrapper.appendChild(progressBar);
            this.progressBar = progressBar;
        }

        // Ottimizza scroll listener con RAF
        let ticking = false;
        let lastScrollLeft = -1;

        this.container.addEventListener('scroll', () => {
            if (!ticking) {
                requestAnimationFrame(() => {
                    // Only update if scroll position actually changed
                    if (this.container.scrollLeft !== lastScrollLeft) {
                        lastScrollLeft = this.container.scrollLeft;
                        this.updateUI();
                    }
                    ticking = false;
                });
                ticking = true;
            }
        }, this.supportsPassive ? { passive: true } : false);
    }

    // Setup accessibilità
    setupAccessibility() {
        // ARIA labels
        this.container.setAttribute('role', 'region');
        this.container.setAttribute('aria-label', 'Contenuti scrollabili orizzontalmente');
        this.container.setAttribute('tabindex', '0'); // Make focusable

        // Live region per annunci
        const liveRegion = document.createElement('div');
        liveRegion.className = 'sr-only';
        liveRegion.setAttribute('role', 'status');
        liveRegion.setAttribute('aria-live', 'polite');
        liveRegion.setAttribute('aria-atomic', 'true');
        liveRegion.style.cssText = `
            position: absolute;
            width: 1px;
            height: 1px;
            padding: 0;
            margin: -1px;
            overflow: hidden;
            clip: rect(0,0,0,0);
            white-space: nowrap;
            border: 0;
        `;

        this.wrapper.appendChild(liveRegion);
        this.liveRegion = liveRegion;
    }

    // Scroll fluido ottimizzato
    smoothScroll(distance) {
        if (this.supportsScrollBehavior) {
            this.container.scrollBy({
                left: distance,
                behavior: 'smooth'
            });
        } else {
            // Fallback animato
            this.animateScroll(this.container.scrollLeft + distance);
        }
    }

    // Animazione scroll per fallback con RAF optimization
    animateScroll(target) {
        const start = this.container.scrollLeft;
        const distance = target - start;

        // Skip animation for very small distances
        if (Math.abs(distance) < 1) {
            this.container.scrollLeft = target;
            this.updateUI();
            return;
        }

        const duration = Math.min(300, Math.abs(distance) * 0.5); // Dynamic duration
        let startTime = null;
        let animationId = null;

        const animation = (currentTime) => {
            if (!startTime) startTime = currentTime;
            const elapsed = currentTime - startTime;
            const progress = Math.min(elapsed / duration, 1);

            // Easing
            const easeInOutQuad = progress < 0.5
                ? 2 * progress * progress
                : 1 - Math.pow(-2 * progress + 2, 2) / 2;

            this.container.scrollLeft = start + distance * easeInOutQuad;

            if (progress < 1) {
                animationId = requestAnimationFrame(animation);
            } else {
                this.updateUI();
            }
        };

        animationId = requestAnimationFrame(animation);

        // Store animation ID for cancellation
        this.currentAnimation = animationId;
    }

    // Metodi di navigazione
    scrollPrev() {
        const scrollAmount = this.isWideScreen 
            ? this.container.clientWidth * 0.5 
            : this.container.clientWidth * 0.8;
        this.smoothScroll(-scrollAmount);
        this.announce('Scorrimento a sinistra');
    }

    scrollNext() {
        const scrollAmount = this.isWideScreen 
            ? this.container.clientWidth * 0.5 
            : this.container.clientWidth * 0.8;
        this.smoothScroll(scrollAmount);
        this.announce('Scorrimento a destra');
    }

    scrollToStart() {
        this.container.scrollTo({
            left: 0,
            behavior: this.supportsScrollBehavior ? 'smooth' : 'auto'
        });
        this.announce('Inizio dei contenuti');
    }

    scrollToEnd() {
        const maxScroll = this.container.scrollWidth - this.container.clientWidth;
        this.container.scrollTo({
            left: maxScroll,
            behavior: this.supportsScrollBehavior ? 'smooth' : 'auto'
        });
        this.announce('Fine dei contenuti');
    }

    // Aggiorna UI con ottimizzazioni
    updateUI() {
        const scrollLeft = this.container.scrollLeft;
        const maxScroll = this.container.scrollWidth - this.container.clientWidth;

        // Aggiorna pulsanti (desktop only)
        if (this.prevButton && this.nextButton && !this.isMobile) {
            const atStart = scrollLeft <= 0;
            const atEnd = scrollLeft >= maxScroll - 1;

            this.prevButton.disabled = atStart;
            this.nextButton.disabled = atEnd;

            // Update visibility
            this.prevButton.style.display = atStart ? 'none' : 'flex';
            this.nextButton.style.display = atEnd ? 'none' : 'flex';

            // Aggiorna ARIA
            this.prevButton.setAttribute('aria-disabled', atStart);
            this.nextButton.setAttribute('aria-disabled', atEnd);
        }

        // Aggiorna progress bar (desktop only)
        if (this.progressBar && maxScroll > 0 && !this.isMobile) {
            const progress = (scrollLeft / maxScroll) * 100;
            this.progressBar.style.width = `${progress}%`;
            this.progressBar.setAttribute('aria-valuenow', Math.round(progress));
        }
    }

    // Annuncia a screen reader
    announce(message) {
        if (this.liveRegion) {
            this.liveRegion.textContent = message;
            // Reset dopo annuncio
            setTimeout(() => {
                this.liveRegion.textContent = '';
            }, 1000);
        }
    }
}

