/**
 * Theme Name: Hello Elementor Villa Stuart - Modular
 * Theme URI: https://www.villastuart.it/
 * Description: Child theme ottimizzato e modulare per Hello Elementor
 * Author: nwdesigns
 * Author URI: https://www.nwdesigns.it/
 * Template: hello-elementor
 * Version: 2.0.0
 * License: GPL v2 or later
 * License URI: https://www.gnu.org/licenses/gpl-2.0.html
 * Text Domain: hello-elementor-child
 * Domain Path: /languages
 * Tags: custom-background, custom-logo, custom-menu, featured-images, threaded-comments, translation-ready
 */

/* ==========================================================================
   Base Styles
   ========================================================================== */

/* Inherit from parent theme */
@import url("../hello-elementor/style.css");

/* ==========================================================================
   Custom Styles
   ========================================================================== */

/* Doctor Grid */
.grid.doctors {
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(280px, 1fr));
  gap: 2rem;
  margin: 2rem 0;
}

.doctor .name {
  font-size: 1.25rem;
  font-weight: 600;
  margin-bottom: 0.5rem;
  color: #333;
}

.doctor .role {
  font-size: 0.9rem;
  color: #666;
  margin-bottom: 0.25rem;
}

.doctor .specialization {
  font-size: 0.85rem;
  color: #999;
  font-style: italic;
}

.doctor .url-equipe {
  display: inline-block;
  margin-top: 1rem;
  color: #0073aa;
  text-decoration: none;
  font-weight: 500;
  transition: color 0.3s ease;
}

.doctor .url-equipe:hover {
  color: #005177;
  text-decoration: underline;
}

/* Related Staff Container */
.related-staff-container {
  margin: 2rem 0;
}

/* Section Titles */
.section-title {
  font-size: 2rem;
  font-weight: 700;
  margin-bottom: 1.5rem;
  color: #333;
  position: relative;
  padding-bottom: 0.5rem;
}

/* Related Terms List */
.related-terms-list {
  list-style: none;
  padding: 0;
  margin: 1rem 0;
  display: flex;
  flex-wrap: wrap;
  gap: 0.5rem;
}

.related-terms-list li {
  background: #f0f0f0;
  padding: 0.5rem 1rem;
  border-radius: 20px;
  transition: all 0.3s ease;
}

.related-terms-list li:hover {
  background: #e0e0e0;
  transform: scale(1.05);
}

.related-terms-list a {
  color: #333;
  text-decoration: none;
  font-size: 0.9rem;
}

/* ==========================================================================
   Responsive Design
   ========================================================================== */

@media (max-width: 768px) {
  .grid.doctors {
    grid-template-columns: 1fr;
    gap: 1rem;
  }

  .section-title {
    font-size: 1.5rem;
  }

  .related-terms-list {
    justify-content: center;
  }
}

@media (max-width: 480px) {
  .doctor {
    padding: 1rem;
  }

  .doctor .name {
    font-size: 1.1rem;
  }
}

.grid.doctors.one {
  grid-template-columns: 1fr !important;
}

/* ==========================================================================
   Print Styles
   ========================================================================== */

@media print {
  .doctor {
    break-inside: avoid;
  }

  .url-equipe {
    display: none !important;
  }
}

/* ==========================================================================
   Accessibility
   ========================================================================== */

/* Focus styles */
a:focus,
button:focus,
input:focus,
select:focus,
textarea:focus {
  outline: 2px solid #0073aa;
  outline-offset: 2px;
}

/* Skip links */
.screen-reader-text {
  clip: rect(1px, 1px, 1px, 1px);
  position: absolute !important;
  height: 1px;
  width: 1px;
  overflow: hidden;
}

.screen-reader-text:focus {
  background-color: #f1f1f1;
  border-radius: 3px;
  box-shadow: 0 0 2px 2px rgba(0, 0, 0, 0.6);
  clip: auto !important;
  color: #21759b;
  display: block;
  font-size: 14px;
  font-weight: bold;
  height: auto;
  left: 5px;
  line-height: normal;
  padding: 15px 23px 14px;
  text-decoration: none;
  top: 5px;
  width: auto;
  z-index: 100000;
}

/* ==========================================================================
   Utility Classes
   ========================================================================== */

.text-center { text-align: center; }
.text-right { text-align: right; }
.text-left { text-align: left; }

.mt-0 { margin-top: 0; }
.mt-1 { margin-top: 0.5rem; }
.mt-2 { margin-top: 1rem; }
.mt-3 { margin-top: 1.5rem; }
.mt-4 { margin-top: 2rem; }

.mb-0 { margin-bottom: 0; }
.mb-1 { margin-bottom: 0.5rem; }
.mb-2 { margin-bottom: 1rem; }
.mb-3 { margin-bottom: 1.5rem; }
.mb-4 { margin-bottom: 2rem; }

.hidden { display: none !important; }
.visible { display: block !important; }

/* ==========================================================================
   Animations
   ========================================================================== */

@keyframes fadeIn {
  from { opacity: 0; }
  to { opacity: 1; }
}

.fade-in {
  animation: fadeIn 0.5s ease-in;
}

@keyframes slideUp {
  from {
    transform: translateY(20px);
    opacity: 0;
  }
  to {
    transform: translateY(0);
    opacity: 1;
  }
}

.slide-up {
  animation: slideUp 0.5s ease-out;
}

