    27‚Üí		/**
    28‚Üí		 * Gestisce l'invio del form
    29‚Üí		 */
    30‚Üí		public function handle_form_submission() {
    31‚Üí			if (!isset($_POST['doctor_menu_order_nonce']) ||
    32‚Üí			    !wp_verify_nonce($_POST['doctor_menu_order_nonce'], 'doctor_menu_order_action')) {
    33‚Üí				return;
    34‚Üí			}
    35‚Üí			
    36‚Üí			if (!current_user_can('manage_options')) {
    37‚Üí				wp_die('Non hai i permessi necessari.');
    38‚Üí			}
    39‚Üí			
    40‚Üí			$action = sanitize_text_field($_POST['action']);
    41‚Üí			
    42‚Üí			switch ($action) {
    43‚Üí				case 'update_menu_order':
    44‚Üí					$this->update_menu_order();
    45‚Üí					break;
    46‚Üí				case 'save_ids':
    47‚Üí					$this->save_priority_ids();
    48‚Üí					break;
    49‚Üí				case 'reset_all':
    50‚Üí					$this->reset_all_menu_order();
    51‚Üí					break;
    52‚Üí			}
    53‚Üí		}
