    55‚Üí		/**
    56‚Üí		 * Salva gli ID con priorit√†
    57‚Üí		 */
    58‚Üí		private function save_priority_ids() {
    59‚Üí			$ids_input = sanitize_textarea_field($_POST['priority_ids']);
    60‚Üí			$ids_array = $this->parse_ids_input($ids_input);
    61‚Üí			
    62‚Üí			if (empty($ids_array)) {
    63‚Üí				add_action('admin_notices', function() {
    64‚Üí					echo '<div class="notice notice-warning"><p>Nessun ID valido inserito.</p></div>';
    65‚Üí				});
    66‚Üí				return;
    67‚Üí			}
    68‚Üí			
    69‚Üí			// Verifica che gli ID esistano e siano del post type corretto
    70‚Üí			$valid_ids = $this->validate_doctor_ids($ids_array);
    71‚Üí			
    72‚Üí			if (empty($valid_ids)) {
    73‚Üí				add_action('admin_notices', function() {
    74‚Üí					echo '<div class="notice notice-error"><p>Nessun ID valido trovato per post di tipo "doctor".</p></div>';
    75‚Üí				});
    76‚Üí				return;
    77‚Üí			}
    78‚Üí			
    79‚Üí			update_option($this->option_name, $valid_ids);
    80‚Üí			
    81‚Üí			$invalid_count = count($ids_array) - count($valid_ids);
    82‚Üí			$message = sprintf(
    83‚Üí				'Salvati %d ID validi. %s',
    84‚Üí				count($valid_ids),
    85‚Üí				$invalid_count > 0 ? "($invalid_count ID non validi ignorati)" : ''
    86‚Üí			);
    87‚Üí			
    88‚Üí			add_action('admin_notices', function() use ($message) {
    89‚Üí				echo "<div class='notice notice-success'><p>$message</p></div>";
    90‚Üí			});
    91‚Üí		}
    92‚Üí		
    93‚Üí		/**
    94‚Üí		 * Aggiorna il menu_order dei post
    95‚Üí		 */
    96‚Üí		private function update_menu_order() {
    97‚Üí			$priority_ids = get_option($this->option_name, array());
    98‚Üí			
    99‚Üí			if (empty($priority_ids)) {
   100‚Üí				add_action('admin_notices', function() {
   101‚Üí					echo '<div class="notice notice-warning"><p>Prima devi salvare gli ID con priorit√†.</p></div>';
   102‚Üí				});
   103‚Üí				return;
   104‚Üí			}
   105‚Üí			
   106‚Üí			// Ottieni tutti i post di tipo "doctor"
   107‚Üí			$doctors = get_posts(array(
   108‚Üí				                     'post_type' => 'doctor',
   109‚Üí				                     'post_status' => 'any',
   110‚Üí				                     'numberposts' => -1,
   111‚Üí				                     'fields' => 'ids'
   112‚Üí			                     ));
   113‚Üí			
   114‚Üí			if (empty($doctors)) {
   115‚Üí				add_action('admin_notices', function() {
   116‚Üí					echo '<div class="notice notice-warning"><p>Nessun post di tipo "doctor" trovato.</p></div>';
   117‚Üí				});
   118‚Üí				return;
   119‚Üí			}
   120‚Üí			
   121‚Üí			$updated_count = 0;
   122‚Üí			$errors = array();
   123‚Üí			
   124‚Üí			foreach ($doctors as $post_id) {
   125‚Üí				$menu_order = in_array($post_id, $priority_ids) ? 0 : 1;
   126‚Üí				
   127‚Üí				$result = wp_update_post(array(
   128‚Üí					                         'ID' => $post_id,
   129‚Üí					                         'menu_order' => $menu_order
   130‚Üí				                         ), true);
   131‚Üí				
   132‚Üí				if (is_wp_error($result)) {
   133‚Üí					$errors[] = sprintf('Errore aggiornando post ID %d: %s', $post_id, $result->get_error_message());
   134‚Üí				} else {
   135‚Üí					$updated_count++;
   136‚Üí				}
   137‚Üí			}
   138‚Üí			
   139‚Üí			if (empty($errors)) {
   140‚Üí				add_action('admin_notices', function() use ($updated_count, $priority_ids) {
   141‚Üí					$priority_count = count($priority_ids);
   142‚Üí					echo "<div class='notice notice-success'><p>Aggiornamento completato! $updated_count post aggiornati ($priority_count con priorit√† alta in testa).</p></div>";
   143‚Üí				});
   144‚Üí			} else {
   145‚Üí				add_action('admin_notices', function() use ($errors) {
   146‚Üí					$error_text = implode('<br>', $errors);
   147‚Üí					echo "<div class='notice notice-error'><p>Errori durante l'aggiornamento:<br>$error_text</p></div>";
   148‚Üí				});
   149‚Üí			}
   150‚Üí		}
