   157‚Üí			
   158‚Üí			$result = $wpdb->update(
   159‚Üí				$wpdb->posts,
   160‚Üí				array('menu_order' => 1),
   161‚Üí				array('post_type' => 'doctor'),
   162‚Üí				array('%d'),
   163‚Üí				array('%s')
   164‚Üí			);
   165‚Üí			
   166‚Üí			if ($result !== false) {
