   177‚Üí		/**
   178‚Üí		 * Parsing degli ID dal textarea
   179‚Üí		 */
   180‚Üí		private function parse_ids_input($input) {
   181‚Üí			// Rimuovi spazi e newline, poi split per virgola, spazio o newline
   182‚Üí			$input = trim($input);
   183‚Üí			$ids = preg_split('/[\s,\n\r]+/', $input, -1, PREG_SPLIT_NO_EMPTY);
   184‚Üí			
   185‚Üí			// Filtra solo numeri validi
   186‚Üí			$ids = array_filter($ids, function($id) {
   187‚Üí				return is_numeric($id) && $id > 0;
   188‚Üí			});
   189‚Üí			
   190‚Üí			return array_map('intval', $ids);
   191‚Üí		}
   192‚Üí		
   193‚Üí		/**
   194‚Üí		 * Valida che gli ID esistano e siano del post type "doctor"
   195‚Üí		 */
   196‚Üí		private function validate_doctor_ids($ids) {
   197‚Üí			global $wpdb;
   198‚Üí			
   199‚Üí			if (empty($ids)) return array();
   200‚Üí			
   201‚Üí			$ids_string = implode(',', array_map('intval', $ids));
   202‚Üí			$valid_ids = $wpdb->get_col($wpdb->prepare("
   203‚Üí            SELECT ID FROM {$wpdb->posts}
   204‚Üí            WHERE ID IN ($ids_string) AND post_type = %s
   205‚Üí        ", 'doctor'));
   206‚Üí			
   207‚Üí			return array_map('intval', $valid_ids);
   208‚Üí		}
