/**
 * Shortcode per visualizzare il titolo del post corrente con link condizionale
 * Basato sul campo ACF 'add_link'
 */
function conditional_heading_shortcode( $atts ) {
	$atts = shortcode_atts( [
		'post_id' => get_the_ID(),
		'tag' => 'h1',
		'class' => 'elementor-heading-title elementor-size-default'
	], $atts );

	if ( ! function_exists( 'get_field' ) ) {
		return '<p style="color: red;">Advanced Custom Fields non √® attivo.</p>';
	}

	$post_id = intval( $atts['post_id'] );
	if ( ! $post_id || ! get_post( $post_id ) ) {
		return '<p style="color: red;">Post non trovato.</p>';
	}

	// Ottieni il titolo del post
	$post_title = get_the_title( $post_id );
	if ( empty( $post_title ) ) {
		return '';
	}

	// Ottieni il permalink del post
	$post_url = get_permalink( $post_id );

	// Verifica il campo ACF add_link
	$add_link = get_field( 'add_link', $post_id );

	// Costruisci l'output
	$tag = esc_attr( $atts['tag'] );
	$class = esc_attr( $atts['class'] );

	if ( $add_link ) {
		// Con link
		$output = '<' . $tag . ' class="' . $class . '">';
		$output .= '<a href="' . esc_url( $post_url ) . '">' . esc_html( $post_title ) . '</a>';
		$output .= '</' . $tag . '>';
	} else {
		// Senza link
		$output = '<' . $tag . ' class="' . $class . '">' . esc_html( $post_title ) . '</' . $tag . '>';
	}

	return $output;
}
add_shortcode( 'conditional_heading', 'conditional_heading_shortcode' );

