/**
 * Compatibilit√† con WordPress core sitemaps
 */
function exclude_noindex_from_wp_sitemap( $args, $post_type ) {
	return exclude_noindex_from_sitemap_query( $args, $post_type );
}
add_filter( 'wp_sitemaps_posts_query_args', 'exclude_noindex_from_wp_sitemap', 10, 2 );

/**
 * Esclude post dalle sitemap core WordPress
 */
function exclude_noindex_posts_from_wp_sitemap( $url_list, $post_type, $page_num ) {
	if ( ! function_exists( 'get_field' ) ) {
		return $url_list;
	}

	foreach ( $url_list as $key => $url_item ) {
		if ( isset( $url_item['loc'] ) ) {
			$post_id = url_to_postid( $url_item['loc'] );
			if ( $post_id && is_post_noindex( $post_id ) ) {
				unset( $url_list[$key] );
			}
		}
	}

	return $url_list;
}
add_filter( 'wp_sitemaps_posts_url_list', 'exclude_noindex_posts_from_wp_sitemap', 10, 3 );

